import React,{useState,useEffect}  from 'react';
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link
} from "react-router-dom";
import axios from 'axios'
import './App.css';
import SearchBar from './component/SearchBar/SearchBar';
import Content from './component/Content/Content'; 
import HomePage from './component/Content/pages/HomePage';
import PokeDetails from './component/Content/pages/PokeDetails';
export const PokeVerisi=React.createContext();
function App() {

  const [pokemon,setPokemon]=useState([]);
  const [typing,setTyping]=useState("");

  useEffect(()=>{
      axios.get(`https://pokeapi.co/api/v2/pokemon?limit=20`)
      .then(res=>{
          setPokemon(res.data.results.filter(pokemon=>pokemon.name.toUpperCase().includes(typing.toUpperCase())))
      })
  },[typing])

  function typingStart(e)
  {
    setTyping(e.target.value);

  }

  return (
    <div className="App">
      <PokeVerisi.Provider value={pokemon} >
      <Router>
          <SearchBar typing={typing} typingStart={typingStart}/>
          <Routes>
          <Route path="/">
            <Content />
          </Route>
          <Route path="/Home">
            <HomePage/>
          </Route>
          <Route path="/pokemon">
            <PokeDetails />
          </Route>
        </Routes>
      </Router>

      </PokeVerisi.Provider>

    </div>
  );
}

export default App;
