  return (
    <div className='pokeDetail'>
        <div className='pokeDetailCart'>
            <div className='pokeImg'>
                <img src={`https://img.pokemondb.net/artwork/large/${pokemon.name}.jpg`} alt={pokemon.name} />
                {useContext(PokeVerisi).map((item,index)=>{
                 return(
            <div className='pokeCart' key={index}>
                <img src={`https://img.pokemondb.net/artwork/large/${item.name}.jpg`} alt={item.name} />
                 {item.name}
            </div>
        )
      })}
            </div>
            <div className='pokeInfo'>bilgiler</div> 
        </div>
    </div>
  )
}

export default PokeDetails
