import React,{useContext}  from 'react';
import '../pages/HomePage.css';
import { PokeVerisi } from '../../../App';
import {
  BrowserRouter as Router,
  Link
} from "react-router-dom";


export default function HomePage() {

  return (
  <Link to={`/pokemon/${PokeVerisi.name}`}>
      <div className='pokeMain'>
      {useContext(PokeVerisi).map((item,index)=>{
        return(
            <div className='pokeCart' key={index}>
                <img src={`https://img.pokemondb.net/artwork/large/${item.name}.jpg`} alt={item.name} />
                 {item.name}
            </div>
        )
      })}
    </div>
  </Link>
  )
}
