import React,{useEffect, useState} from 'react';
import '../pages/PokeDetails.css';
import {useParams} from "react-router-dom";
import {MdTrendingUp} from "react-icons/md";
import {Link} from 'react-router-dom'
import axios from 'axios';


function PokeDetails() {
  let {pokemonName} = useParams();
  const [pokemonData,setPokemonData]=useState(null);

  useEffect(() => {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${pokemonName}`)
    .then(res=>{setPokemonData(res.data)
    })
  }, [pokemonName])
  
console.log(pokemonData)
  return (
    <div className='pokeDetail'>
      <div className='title'>
        <h1 >{pokemonName.toUpperCase()}</h1>
        <Link to={'/'}>
            <span><MdTrendingUp/> </span>
        </Link>
      </div>
        <div className='pokeDetailCart'>
            <div className='pokeImg'>
                <img src={`https://img.pokemondb.net/artwork/large/${pokemonName}.jpg`} alt={pokemonName} />
            </div>
            <div className='pokeInfo'>
              <div className='bio'>
                <p>Hakkında</p>
              </div>
              <div className='features'>
                {pokemonData && pokemonData.stats.map(item=>{
                  return(
                    <div>{item.stat.name}---->{item.base_stat}</div>
                  )
                })}
              </div>
            </div>
        </div>
    </div>
  )
}

export default PokeDetails
