import React,{useState,useEffect}  from 'react';
import axios from 'axios'
import './App.css';
import SearchBar from './component/SearchBar/SearchBar';
import Content from './component/Content/Content'; 
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
export const PokeVerisi=React.createContext();
function App() {

  const [pokemon,setPokemon]=useState([]);
  const [typing,setTyping]=useState("");


  useEffect(()=>{
      axios.get(`https://pokeapi.co/api/v2/pokemon?limit=500`)
      .then(res=>{
          setPokemon(res.data.results.filter(pokemon=>pokemon.name.toUpperCase().includes(typing.toUpperCase())))
      })
  },[typing])

  function typingStart(e)
  {
    setTyping(e.target.value);

  }

  return (
      <div className="App">
        <PokeVerisi.Provider value={pokemon} >
        <Router>
            <SearchBar typing={typing} typingStart={typingStart}/>
            <Content />
          <Switch>
              <Route path="/about">
                <About />
              </Route>
              <Route path="/users">
                <Users />
              </Route>
              <Route path="/">
                <Home />
              </Route>
            </Switch>
        </Router>
    </PokeVerisi.Provider>
      </div>
  );
}

export default App;
