import React from 'react';
import '../pages/PokeDetails.css';
import {useParams} from "react-router-dom";
import {MdTrendingUp} from "react-icons/md";
import {Link} from 'react-router-dom'

function PokeDetails(props) {
  let {pokemonName} = useParams();
  console.log(props.pokemon)
  return (
    <div className='pokeDetail'>
      <div className='title'>
        <h1>{pokemonName.toUpperCase()}</h1>
        <Link to={'/'}>
            <span><MdTrendingUp/> </span>
        </Link>
      </div>
        <div className='pokeDetailCart'>
            <div className='pokeImg'>
                <img src={`https://img.pokemondb.net/artwork/large/${pokemonName}.jpg`} alt={"charmeleon"} />
            </div>
            <div className='pokeInfo'>{pokemonName}</div>
        </div>
    </div>
  )
}

export default PokeDetails
