import React,{useState,useEffect}  from 'react';
import axios from 'axios'
import './App.css';
import SearchBar from './component/SearchBar/SearchBar';
import Content from './component/Content/Content'; 
export const PokeVerisi=React.createContext();
function App() {

  const [pokemon,setPokemon]=useState([]);
  const [typing,setTyping]=useState("");
  const [offset,setOffset]=useState(0);
  const [limit,setLimit]=useState(0);
  const [url,setUrl]=useState(`https://pokeapi.co/api/v2/pokemon?offset=${offset}&limit=${limit}`)

  useEffect(()=>{
      axios.get(url)
      .then(res=>{
          // setPokemon(res.data.results.filter(pokemon=>pokemon.name.includes(typing)))
          setPokemon(res.data.results.filter(pokemon=>pokemon.name.toUpperCase().includes(typing.toUpperCase())))
      })
  },[typing])

  function typingStart(e)
  {
    setTyping(e.target.value);

  }
  const next = async () => {
    setOffset(offset+20);
    setLimit(limit+20);
    setUrl(`https://pokeapi.co/api/v2/pokemon?offset=${offset}&limit=${limit}`)
  }

  return (
    <div className="App">
      <PokeVerisi.Provider value={pokemon} >
          <SearchBar typing={typing} typingStart={typingStart}/>
          <Content next={next}/>
      </PokeVerisi.Provider>

    </div>
  );
}

export default App;
