        return res;
    }

  return (
    <div className='pokeDetail'>
        <div className='pokeDetailCart'>
            <div className='pokeImg'>
                <img src={`https://img.pokemondb.net/artwork/large/charmeleon.jpg`} alt={"charmeleon"} />
            </div>
            <div className='pokeInfo'>bilgiler</div> 
        </div>
    </div>
  )
}

